    public class CorePlan
    {

        //프로퍼티

        public List<Curve> walls { get; private set; }
        public List<Curve> normals { get; private set; }
        public List<Curve> others { get; private set; }
        public List<Curve> groundFloor { get; private set; }

        //생성자

        private CorePlan()
        {

        }

        public CorePlan(Core core)
        {
            CoreType coreType = core.CoreType;
            Vector3d xVector = core.XDirection;
            Vector3d yVector = core.YDirection;
            Point3d origin = core.Origin;
            double coreInterpenetration = core.CoreInterpenetration;

            string[] fixedAndFlex = GetCoreString(coreType).Split('&');

            xVector = xVector / xVector.Length;
            yVector = yVector / yVector.Length;

            List<Curve> tempWalls = new List<Curve>();
            List<Curve> tempNormals = new List<Curve>();
            List<Curve> tempOthers = new List<Curve>();
            List<Curve> tempGroundFloor = new List<Curve>();


            if (fixedAndFlex[0] != "")
            {
                string[] eachFixedLineString = fixedAndFlex[0].Split('/');

                for (int i = 0; i < eachFixedLineString.Length; i++)
                {
                    string[] tempLineComponents = eachFixedLineString[i].Split(',');

                    Point3d tempStart = (new Point3d(xVector * double.Parse(tempLineComponents[0]) + yVector * double.Parse(tempLineComponents[1]))) + origin;
                    Point3d tempEnd = (new Point3d(xVector * double.Parse(tempLineComponents[3]) + yVector * double.Parse(tempLineComponents[4]))) + origin;

                    Curve lineCurve = new LineCurve(tempStart, tempEnd);

                    if (tempLineComponents[6] == "wall")
                        tempWalls.Add(lineCurve);
                    else if (tempLineComponents[6] == "normal")
                        tempNormals.Add(lineCurve);
                    else if (tempLineComponents[6] == "others")
                        tempOthers.Add(lineCurve);
                    else if (tempLineComponents[6] == "groundFloor")
                        tempGroundFloor.Add(lineCurve);

                }
            }

            if (fixedAndFlex[1] != "")
            {
                string[] eachFlexibleLineString = fixedAndFlex[1].Split('/');

                for (int i = 0; i < eachFlexibleLineString.Length; i++)
                {
                    string[] tempLineComponents = eachFlexibleLineString[i].Split(',');

                    Point3d tempStart = (new Point3d(xVector * double.Parse(tempLineComponents[0]) + yVector * double.Parse(tempLineComponents[1]))) + origin;
                    Vector3d tempVector = xVector * double.Parse(tempLineComponents[3]) + yVector * double.Parse(tempLineComponents[4]);
                    double Length = double.Parse(tempLineComponents[6]) - coreInterpenetration;

                    Point3d tempEnd = tempStart + tempVector * Length;

                    tempWalls.Add(new LineCurve(tempStart, tempEnd));
                }
            }

            this.walls = new List<Curve>(tempWalls);
            this.normals = new List<Curve>(tempNormals);
            this.others = new List<Curve>(tempOthers);
            this.groundFloor = new List<Curve>(tempGroundFloor);

        }

